mapper.select:
SELECT 
FROM repository_duplicate_doc_mapping
WHERE del_flg = 0

    AND repository_duplicate_id = #{repositoryDuplicateId}


    AND repository_doc_id = #{repositoryDocId}

mapper.insert:
INSERT INTO repository_duplicate_doc_mapping (
    repository_duplicate_id, repository_doc_id,
    created_by, created_at, updated_by, updated_at, del_flg
) VALUES (
             #{repositoryDuplicateId}, #{repositoryDocId},
             #{createdBy}, #{createdAt}, #{updatedBy}, #{updatedAt}, #{delFlg}
         )

INSERT INTO repository_duplicate_doc_mapping (
repository_doc_id, repository_duplicate_id,
created_by, created_at, updated_by, updated_at, del_flg
)
VALUES

    (
    #{item.repositoryDocId}, #{item.repositoryDuplicateId},
    #{item.createdBy}, #{item.createdAt},
    #{item.updatedBy}, #{item.updatedAt}, #{item.delFlg}
    )

mapper.update:
UPDATE repository_duplicate_doc_mapping
repository_doc_id = #{repositoryDocId}, updated_by = #{updatedBy}, updated_at = #{updatedAt}, del_flg = #{delFlg},
WHERE repository_duplicate_id = #{repositoryDuplicateId}
