mapper.select:
SELECT 
FROM repository_duplicate_extract_mapping
WHERE del_flg = 0

    AND repository_duplicate_id = #{repositoryDuplicateId}


    AND rule_extract_id = #{ruleExtractId}

mapper.insert:
INSERT INTO repository_duplicate_extract_mapping (
    id, repository_duplicate_id, rule_extract_id, created_by, created_at, updated_by, updated_at, del_flg
) VALUES (
             #{id}, #{repositoryDuplicateId}, #{ruleExtractId}, #{createdBy}, #{createdAt}, #{updatedBy}, #{updatedAt}, #{delFlg}
         )
