mapper.select:
SELECT 
FROM repository_review_doc_mapping
WHERE del_flg = 0

    AND repository_review_id = #{repositoryReviewId}


    AND repository_doc_id = #{repositoryDocId}

mapper.insert:
INSERT INTO repository_review_doc_mapping (
    repository_review_id, repository_doc_id,
    created_by, created_at, updated_by, updated_at, del_flg
) VALUES (
             #{repositoryReviewId}, #{repositoryDocId},
             #{createdBy}, #{createdAt}, #{updatedBy}, #{updatedAt}, #{delFlg}
         )

INSERT INTO repository_review_doc_mapping (
repository_doc_id, repository_review_id,
created_by, created_at, updated_by, updated_at, del_flg
)
VALUES

    (
    #{item.repositoryDocId}, #{item.repositoryReviewId},
    #{item.createdBy}, #{item.createdAt},
    #{item.updatedBy}, #{item.updatedAt}, #{item.delFlg}
    )

mapper.update:
UPDATE repository_review_doc_mapping
SET
    status = CASE repository_doc_id

        WHEN #{item.repositoryDocId} THEN #{item.status}

    END,
    del_flg = CASE repository_doc_id

        WHEN #{item.repositoryDocId} THEN #{item.delFlg}

END
WHERE
    repository_review_id = #{repositoryReviewId}
AND repository_doc_id IN

        #{item.ruleExtractId}

UPDATE repository_review_doc_mapping
del_flg = #{delFlg}, updated_by = #{updatedBy}, updated_at = #{updatedAt},
WHERE repository_review_id = #{repositoryReviewId}
