mapper.select:
SELECT 
FROM repository_review_extract_mapping
WHERE del_flg = 0

    AND repository_review_id = #{repositoryReviewId}


    AND rule_extract_id = #{ruleExtractId}

mapper.insert:
INSERT INTO repository_review_extract_mapping (
    id, repository_review_id, rule_extract_id, created_by, created_at, updated_by, updated_at, del_flg
) VALUES (
             #{id}, #{repositoryReviewId}, #{ruleExtractId}, #{createdBy}, #{createdAt}, #{updatedBy}, #{updatedAt}, #{delFlg}
         )

mapper.update:
UPDATE repository_review_extract_mapping
SET
    status = CASE rule_extract_id

            WHEN #{item.ruleExtractId} THEN #{item.status}

    END,
    del_flg = CASE rule_extract_id

            WHEN #{item.ruleExtractId} THEN #{item.delFlg}

    END
WHERE
    repository_review_id = #{repositoryReviewId}
    AND rule_extract_id IN

        #{item.ruleExtractId}

UPDATE repository_review_extract_mapping
del_flg = #{delFlg}, updated_by = #{updatedBy}, updated_at = #{updatedAt},
WHERE repository_review_id = #{repositoryReviewId}
