mapper.select:
SELECT 
FROM task_review_rule_mapping
WHERE del_flg = 0

    AND task_review_id = #{taskReviewId}


    AND rule_review_id = #{ruleReviewId}

SELECT COUNT(1) FROM task_review_rule_mapping


    AND task_review_id = #{taskReviewId}


    AND rule_review_id = #{ruleReviewId}


    AND compliant_flg = #{compliantFlg}


    AND del_flg = #{delFlg}

SELECT 
FROM task_review_rule_mapping


        AND task_review_id = #{taskReviewId}


        AND rule_review_id = #{ruleReviewId}


        AND compliant_flg = #{compliantFlg}


        AND question_msg = #{questionMsg}


        AND advice = #{advice}


        AND created_by = #{createdBy}


        AND updated_by = #{updatedBy}


        AND del_flg = #{delFlg}

mapper.insert:
INSERT INTO task_review_rule_mapping (
    task_review_id, rule_review_id, compliant_flg, question_msg, advice,
    created_by, created_at, updated_by, updated_at, del_flg
) VALUES (
             #{taskReviewId}, #{ruleReviewId}, #{compliantFlg}, #{questionMsg}, #{advice},
             #{createdBy}, #{createdAt}, #{updatedBy}, #{updatedAt}, #{delFlg}
         )

INSERT INTO task_review_rule_mapping (
task_review_id, rule_review_id, compliant_flg, question_msg, advice,
created_by, created_at, updated_by, updated_at, del_flg
)
VALUES

    (
        #{item.taskReviewId}, #{item.ruleReviewId}, #{item.compliantFlg}, #{item.questionMsg}, #{item.advice},
        #{item.createdBy}, #{item.createdAt},
        #{item.updatedBy}, #{item.updatedAt}, #{item.delFlg}
    )

mapper.update:
UPDATE task_review_rule_mapping
task_review_id = #{taskReviewId}, rule_review_id = #{ruleReviewId}, compliant_flg = #{compliantFlg}, question_msg = #{questionMsg}, advice = #{advice}, updated_by = #{updatedBy}, updated_at = #{updatedAt}, del_flg = #{delFlg},
WHERE id = #{id}

UPDATE task_review_rule_mapping
rule_review_id = #{ruleReviewId}, compliant_flg = #{compliantFlg}, question_msg = #{questionMsg}, advice = #{advice}, updated_by = #{updatedBy}, updated_at = #{updatedAt}, del_flg = #{delFlg},
WHERE task_review_id = #{taskReviewId}
